// +build dev

package swagger

import (
	"net/http"
	"os"
	"strings"

	"github.com/shurcooL/httpfs/filter"
)

// Assets contains project assets.
var (
	Assets http.FileSystem = filter.Skip(http.Dir("./swagger/api"), func(path string, fi os.FileInfo) bool {
		// ignore the autogenerated flipt.swagger.json in favor of the hand edited version
		return strings.HasSuffix(path, "/flipt.swagger.json")
	})
)
